    // PlainText^i = Decription^key(Cipher^i) = (cipher^i - Key^i + 26) mod 26
    public static void decrypt( String text, String key){
        String result= "";

        for (int i = 0, j = 0 ; i< text.length(); i++){

            // check if the char is between A-Z
            char b = text.charAt(i);
            //make sure the characters are in the alphabet
            if (b < 'A' || b > 'Z') continue;

            result += (char) (((b - key.charAt(j) + 26) % 26) + 'A');
            j = ++j %key.length();
        }
        System.out.println(result);
    }


